import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import GameLobby from './components/GameLobby';
import GameRoom from './components/GameRoom';
import './App.css';

const socket = io('http://localhost:5000');

function App() {
  const [gameState, setGameState] = useState('lobby'); // lobby, room, playing
  const [playerName, setPlayerName] = useState('');
  const [roomId, setRoomId] = useState('');
  const [players, setPlayers] = useState([]);
  const [currentPlayer, setCurrentPlayer] = useState(null);

  useEffect(() => {
    socket.on('room_update', (data) => {
      console.log('Room update received:', data);
      setPlayers(data.players);
      // Only set to room state if we're not already playing
      if (gameState !== 'playing') {
        setGameState('room');
      }
    });

    socket.on('game_started', (data) => {
      console.log('Received game_started event:', data);
      setGameState('playing');
      setPlayers(data.players);
      console.log('Set game state to playing');
    });

    return () => {
      socket.off('room_update');
      socket.off('game_started');
    };
  }, [gameState]);

  const joinRoom = (name, room) => {
    setPlayerName(name);
    setRoomId(room);
    setCurrentPlayer({ id: socket.id, name });
    socket.emit('join_room', { roomId: room, playerName: name });
  };

  const startGame = () => {
    console.log('Start Game clicked! Room ID:', roomId);
    console.log('Socket connected:', socket.connected);
    console.log('Socket ID:', socket.id);
    socket.emit('start_game', { roomId });
    console.log('Emitted start_game event');
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>AI Prompt Guesser</h1>
        {/* Debug info */}
        <div style={{fontSize: '12px', background: 'rgba(0,0,0,0.3)', padding: '5px'}}>
          App State: {gameState} | Players: {players.length} | Room: {roomId}
          <button onClick={() => {
            setGameState('playing');
            // Simulate the game_started event data
            const fakeGameData = {
              currentPromptGiver: players[0]?.id || socket.id,
              players: players.map(p => ({...p, isPromptGiver: p.id === (players[0]?.id || socket.id)})),
              round: 1
            };
            // Manually trigger the game_started event
            socket.emit('fake_game_started', fakeGameData);
          }} style={{marginLeft: '10px', fontSize: '10px'}}>
            Force Start (Debug)
          </button>
        </div>
      </header>
      
      {gameState === 'lobby' && (
        <GameLobby onJoinRoom={joinRoom} />
      )}
      
      {gameState === 'room' && (
        <GameRoom 
          players={players}
          currentPlayer={currentPlayer}
          roomId={roomId}
          onStartGame={startGame}
          socket={socket}
        />
      )}
      
      {gameState === 'playing' && (
        <GameRoom 
          players={players}
          currentPlayer={currentPlayer}
          roomId={roomId}
          socket={socket}
          isPlaying={true}
        />
      )}
    </div>
  );
}

export default App;
