import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import GameLobby from './components/GameLobby';
import GameRoom from './components/GameRoom';
import './App.css';

const socket = io('http://localhost:5000');

function App() {
  const [gameState, setGameState] = useState('lobby'); // lobby, room, playing
  const [playerName, setPlayerName] = useState('');
  const [roomId, setRoomId] = useState('');
  const [players, setPlayers] = useState([]);
  const [currentPlayer, setCurrentPlayer] = useState(null);

  useEffect(() => {
    socket.on('room_update', (data) => {
      setPlayers(data.players);
      setGameState(data.gameState === 'waiting' ? 'room' : 'playing');
    });

    socket.on('game_started', (data) => {
      setGameState('playing');
      setPlayers(data.players);
    });

    return () => {
      socket.off('room_update');
      socket.off('game_started');
    };
  }, []);

  const joinRoom = (name, room) => {
    setPlayerName(name);
    setRoomId(room);
    setCurrentPlayer({ id: socket.id, name });
    socket.emit('join_room', { roomId: room, playerName: name });
  };

  const startGame = () => {
    socket.emit('start_game', { roomId });
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>AI Prompt Guesser</h1>
      </header>
      
      {gameState === 'lobby' && (
        <GameLobby onJoinRoom={joinRoom} />
      )}
      
      {gameState === 'room' && (
        <GameRoom 
          players={players}
          currentPlayer={currentPlayer}
          roomId={roomId}
          onStartGame={startGame}
          socket={socket}
        />
      )}
      
      {gameState === 'playing' && (
        <GameRoom 
          players={players}
          currentPlayer={currentPlayer}
          roomId={roomId}
          socket={socket}
          isPlaying={true}
        />
      )}
    </div>
  );
}

export default App;
