import React, { useState, useEffect } from 'react';
import PromptInput from './PromptInput';
import GuessInput from './GuessInput';
import PlayerList from './PlayerList';
import GameImage from './GameImage';

function GameRoom({ players, currentPlayer, roomId, onStartGame, socket, isPlaying = false }) {
  const [gameData, setGameData] = useState({
    currentPromptGiver: null,
    currentImage: '',
    currentPrompt: '',
    timeRemaining: 60,
    round: 0,
    maxRounds: 5,
    gameState: 'waiting'
  });
  const [guesses, setGuesses] = useState([]);

  useEffect(() => {
    socket.on('game_started', (data) => {
      setGameData(prev => ({
        ...prev,
        currentPromptGiver: data.currentPromptGiver,
        round: data.round,
        gameState: 'waiting_for_prompt'
      }));
    });

    socket.on('next_round', (data) => {
      setGameData(prev => ({
        ...prev,
        currentPromptGiver: data.currentPromptGiver,
        round: data.round,
        gameState: 'waiting_for_prompt',
        currentImage: '',
        currentPrompt: '',
        timeRemaining: 60
      }));
      setGuesses([]);
    });

    socket.on('prompt_submitted', (data) => {
      setGameData(prev => ({
        ...prev,
        currentImage: data.imageUrl,
        gameState: 'guessing',
        timeRemaining: 60
      }));
    });

    socket.on('guess_submitted', (data) => {
      setGuesses(prev => [...prev, data]);
    });

    socket.on('round_ended', (data) => {
      setGameData(prev => ({
        ...prev,
        currentPrompt: data.originalPrompt,
        gameState: 'round_ended'
      }));
      
      // Update guesses with results
      if (data.results) {
        setGuesses(data.results);
      }
    });

    socket.on('game_finished', (data) => {
      setGameData(prev => ({
        ...prev,
        gameState: 'finished'
      }));
    });

    socket.on('timer_update', (data) => {
      setGameData(prev => ({
        ...prev,
        timeRemaining: data.timeRemaining
      }));
    });

    return () => {
      socket.off('game_started');
      socket.off('next_round');
      socket.off('prompt_submitted');
      socket.off('guess_submitted');
      socket.off('round_ended');
      socket.off('game_finished');
      socket.off('timer_update');
    };
  }, [socket]);

  const isCurrentPlayerPromptGiver = () => {
    const player = players.find(p => p.id === currentPlayer?.id);
    return player?.isPromptGiver || false;
  };

  const canStartGame = players.length >= 2;

  if (!isPlaying) {
    return (
      <div className="game-room">
        <div className="room-header">
          <h2>Room: {roomId}</h2>
          <p>Players: {players.length}</p>
        </div>
        
        <PlayerList players={players} />
        
        {canStartGame && (
          <button onClick={onStartGame} className="start-game-btn">
            Start Game
          </button>
        )}
        
        {!canStartGame && (
          <p className="waiting-message">
            Waiting for at least 2 players to start...
          </p>
        )}
      </div>
    );
  }

  return (
    <div className="game-room playing">
      <div className="game-header">
        <div className="round-info">
          <span>Round {gameData.round}/{gameData.maxRounds}</span>
          <span className="timer">Time: {gameData.timeRemaining}s</span>
        </div>
      </div>
      
      <div className="game-content">
        <div className="left-panel">
          <PlayerList players={players} showScores={true} />
        </div>
        
        <div className="center-panel">
          {/* Debug info - remove this later */}
          <div style={{background: 'rgba(0,0,0,0.3)', padding: '10px', marginBottom: '10px', fontSize: '12px'}}>
            <div>Game State: {gameData.gameState}</div>
            <div>Current Player: {currentPlayer?.name}</div>
            <div>Is Prompt Giver: {isCurrentPlayerPromptGiver() ? 'YES' : 'NO'}</div>
            <div>Round: {gameData.round}</div>
          </div>
          
          {gameData.gameState === 'waiting_for_prompt' && isCurrentPlayerPromptGiver() && (
            <PromptInput socket={socket} roomId={roomId} />
          )}
          
          {gameData.gameState === 'waiting_for_prompt' && !isCurrentPlayerPromptGiver() && (
            <div className="waiting-prompt">
              <p>Waiting for prompt giver to submit their prompt...</p>
            </div>
          )}
          
          {gameData.currentImage && (
            <GameImage imageUrl={gameData.currentImage} />
          )}
          
          {gameData.gameState === 'round_ended' && (
            <div className="round-results">
              <h3>Round Results</h3>
              <p><strong>Original Prompt:</strong> {gameData.currentPrompt}</p>
              <div className="guesses-results">
                {guesses.map((guess, index) => (
                  <div key={index} className="guess-result">
                    <span>{guess.playerName}: "{guess.text}"</span>
                    <span className="score">+{guess.points} points</span>
                  </div>
                ))}
              </div>
            </div>
          )}
          
          {gameData.gameState === 'finished' && (
            <div className="game-finished">
              <h2>Game Finished!</h2>
              <div className="final-scores">
                {players
                  .sort((a, b) => b.score - a.score)
                  .map((player, index) => (
                    <div key={player.id} className={`final-score ${index === 0 ? 'winner' : ''}`}>
                      <span>{index + 1}. {player.name}</span>
                      <span>{player.score} points</span>
                    </div>
                  ))}
              </div>
            </div>
          )}
        </div>
        
        <div className="right-panel">
          {gameData.gameState === 'guessing' && !isCurrentPlayerPromptGiver() && (
            <GuessInput socket={socket} roomId={roomId} />
          )}
          
          <div className="guesses-list">
            <h4>Guesses:</h4>
            {guesses.map((guess, index) => (
              <div key={index} className="guess-item">
                <strong>{guess.playerName}:</strong> {guess.text}
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default GameRoom;
