import React, { useState } from 'react';

function PromptInput({ socket, roomId }) {
  const [prompt, setPrompt] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const maxWords = 5;

  const getWordCount = (text) => {
    return text.trim().split(/\s+/).filter(word => word.length > 0).length;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const wordCount = getWordCount(prompt);
    
    if (wordCount > maxWords) {
      alert(`Prompt must be ${maxWords} words or less!`);
      return;
    }
    
    if (prompt.trim()) {
      setIsSubmitting(true);
      socket.emit('submit_prompt', { roomId, prompt: prompt.trim() });
    }
  };

  const wordCount = getWordCount(prompt);
  const isOverLimit = wordCount > maxWords;

  return (
    <div className="prompt-input-container">
      <h3>Your turn to create a prompt!</h3>
      <p>Create a prompt for the AI to generate an image (max {maxWords} words)</p>
      
      <form onSubmit={handleSubmit}>
        <div className="input-group">
          <textarea
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
            placeholder="Enter your prompt here..."
            disabled={isSubmitting}
            rows={3}
            maxLength={100}
          />
          <div className={`word-count ${isOverLimit ? 'over-limit' : ''}`}>
            {wordCount}/{maxWords} words
          </div>
        </div>
        
        <button 
          type="submit" 
          disabled={!prompt.trim() || isOverLimit || isSubmitting}
          className="submit-prompt-btn"
        >
          {isSubmitting ? 'Generating Image...' : 'Submit Prompt'}
        </button>
      </form>
      
      <div className="prompt-tips">
        <h4>Tips for good prompts:</h4>
        <ul>
          <li>Be descriptive but concise</li>
          <li>Include objects, colors, or settings</li>
          <li>Avoid overly abstract concepts</li>
          <li>Think about what would be fun to guess!</li>
        </ul>
      </div>
    </div>
  );
}

export default PromptInput;
