import React, { useState } from 'react';

function GuessInput({ socket, roomId }) {
  const [guess, setGuess] = useState('');
  const [hasSubmitted, setHasSubmitted] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (guess.trim() && !hasSubmitted) {
      socket.emit('submit_guess', { roomId, guess: guess.trim() });
      setHasSubmitted(true);
      setGuess('');
    }
  };

  return (
    <div className="guess-input-container">
      <h4>What was the prompt?</h4>
      
      {!hasSubmitted ? (
        <form onSubmit={handleSubmit}>
          <div className="input-group">
            <input
              type="text"
              value={guess}
              onChange={(e) => setGuess(e.target.value)}
              placeholder="Enter your guess..."
              maxLength={50}
              autoFocus
            />
          </div>
          <button type="submit" disabled={!guess.trim()}>
            Submit Guess
          </button>
        </form>
      ) : (
        <div className="guess-submitted">
          <p>âœ“ Guess submitted!</p>
          <p>Waiting for round to end...</p>
        </div>
      )}
    </div>
  );
}

export default GuessInput;
